import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const initialState = {
  nome: "",
  nascimento: "",
  cpf: "",
  email: "",
  telefone: "",
  cep: "",
  endereco: "",
  numero: "",
  complemento: "",
  bairro: "",
  estado: "",
  cidade: ""
};

const estados = ["SP", "RJ", "MG", "RS", "PR", "SC", "BA", "PE", "CE", "GO"];
const cidades = ["São Paulo", "Rio de Janeiro", "Belo Horizonte", "Porto Alegre", "Curitiba", "Salvador", "Recife", "Fortaleza", "Goiânia"];

const Adotar = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState(initialState);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    alert("Solicitação enviada!");
    setForm(initialState);
    navigate("/animais");
  };

  return (
    <main>
      <h1 style={{ color: "#2563eb", textAlign: "center", marginBottom: "2rem" }}>Formulário de Adoção</h1>
      <form
        onSubmit={handleSubmit}
        style={{
          background: "#f8fae5",
          padding: "2rem",
          borderRadius: "16px",
          maxWidth: 900,
          margin: "0 auto",
          display: "flex",
          flexDirection: "column",
          gap: "1.5rem",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)"
        }}
      >
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <div style={{ flex: 2, minWidth: 220 }}>
            <label style={{ fontSize: "0.95rem" }}>Nome completo</label>
            <input
              name="nome"
              value={form.nome}
              onChange={handleChange}
              placeholder="Insira seu nome completo"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
          <div style={{ flex: 1, minWidth: 160 }}>
            <label style={{ fontSize: "0.95rem" }}>Data de nascimento</label>
            <input
              name="nascimento"
              value={form.nascimento}
              onChange={handleChange}
              placeholder="__/__/____"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
        </div>
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <div style={{ flex: 1, minWidth: 120 }}>
            <label style={{ fontSize: "0.95rem" }}>CPF</label>
            <input
              name="cpf"
              value={form.cpf}
              onChange={handleChange}
              placeholder="___.___.___-__"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
          <div style={{ flex: 2, minWidth: 220 }}>
            <label style={{ fontSize: "0.95rem" }}>E-mail</label>
            <input
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Insira seu email"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
          <div style={{ flex: 1, minWidth: 160 }}>
            <label style={{ fontSize: "0.95rem" }}>Telefone</label>
            <input
              name="telefone"
              value={form.telefone}
              onChange={handleChange}
              placeholder="(__) _____-____"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
        </div>
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <div style={{ flex: 1, minWidth: 120 }}>
            <label style={{ fontSize: "0.95rem" }}>CEP</label>
            <input
              name="cep"
              value={form.cep}
              onChange={handleChange}
              placeholder="_____-___"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
          <div style={{ flex: 3, minWidth: 220 }}>
            <label style={{ fontSize: "0.95rem" }}>Endereço</label>
            <input
              name="endereco"
              value={form.endereco}
              onChange={handleChange}
              placeholder="Insira seu endereço"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
        </div>
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <div style={{ flex: 1, minWidth: 120 }}>
            <label style={{ fontSize: "0.95rem" }}>Número</label>
            <input
              name="numero"
              value={form.numero}
              onChange={handleChange}
              placeholder="Insira o número"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
          <div style={{ flex: 2, minWidth: 160 }}>
            <label style={{ fontSize: "0.95rem" }}>Complemento</label>
            <input
              name="complemento"
              value={form.complemento}
              onChange={handleChange}
              placeholder="Insira o complemento"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
            />
          </div>
          <div style={{ flex: 2, minWidth: 160 }}>
            <label style={{ fontSize: "0.95rem" }}>Bairro</label>
            <input
              name="bairro"
              value={form.bairro}
              onChange={handleChange}
              placeholder="Insira o bairro"
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            />
          </div>
        </div>
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <div style={{ flex: 1, minWidth: 220 }}>
            <label style={{ fontSize: "0.95rem" }}>Estado</label>
            <select
              name="estado"
              value={form.estado}
              onChange={handleChange}
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            >
              <option value="">Selecione o Estado</option>
              {estados.map(uf => (
                <option key={uf} value={uf}>{uf}</option>
              ))}
            </select>
          </div>
          <div style={{ flex: 2, minWidth: 220 }}>
            <label style={{ fontSize: "0.95rem" }}>Cidade</label>
            <select
              name="cidade"
              value={form.cidade}
              onChange={handleChange}
              style={{ width: "100%", padding: "1rem", borderRadius: 8, border: "1px solid #ccc", fontSize: "1.1rem" }}
              required
            >
              <option value="">Selecione a Cidade</option>
              {cidades.map(cidade => (
                <option key={cidade} value={cidade}>{cidade}</option>
              ))}
            </select>
          </div>
        </div>
        <div style={{ display: "flex", gap: "1rem", marginTop: "2rem" }}>
          <button
            type="button"
            onClick={() => navigate(-1)}
            style={{
              flex: 1,
              background: "#fff",
              color: "#009ca6",
              border: "2px solid #009ca6",
              borderRadius: 10,
              padding: "1rem",
              fontWeight: "bold",
              fontSize: "1.3rem",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center"
            }}
          >
            &#8592; Voltar
          </button>
          <button
            type="submit"
            style={{
              flex: 1,
              background: "#009ca6",
              color: "#fff",
              border: "none",
              borderRadius: 10,
              padding: "1rem",
              fontWeight: "bold",
              fontSize: "1.3rem",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center"
            }}
          >
            Prosseguir &#8594;
          </button>
        </div>
      </form>
    </main>
  );
};

export default Adotar;
