import React, { useState } from "react";

const Home = () => {
  const [form, setForm] = useState({ nome: "", email: "", mensagem: "" });
  const [enviado, setEnviado] = useState(false);
  const [erro, setErro] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.nome || !form.email || !form.mensagem) {
      setErro("Preencha todos os campos.");
      return;
    }
    setErro("");
    setEnviado(true);
    setForm({ nome: "", email: "", mensagem: "" });
  };

  return (
    <main>
      <h1>Bem-vindo ao Catálogo de Animais para Adoção!</h1>
      <p>Entre em contato conosco para dúvidas, sugestões ou parcerias:</p>
      <form
        onSubmit={handleSubmit}
        style={{
          background: "#f1f5f9",
          padding: "1.5rem",
          borderRadius: "10px",
          maxWidth: "400px",
          margin: "2rem auto 0 auto",
          display: "flex",
          flexDirection: "column",
          gap: "1rem",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)"
        }}
      >
        <input
          type="text"
          name="nome"
          placeholder="Seu nome"
          value={form.nome}
          onChange={handleChange}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <input
          type="email"
          name="email"
          placeholder="Seu e-mail"
          value={form.email}
          onChange={handleChange}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <textarea
          name="mensagem"
          placeholder="Sua mensagem"
          value={form.mensagem}
          onChange={handleChange}
          rows={4}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        {erro && <span style={{ color: "red" }}>{erro}</span>}
        {enviado && <span style={{ color: "green" }}>Mensagem enviada!</span>}
        <button
          type="submit"
          style={{
            background: "#2563eb",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            padding: "0.8rem",
            fontWeight: "bold",
            cursor: "pointer"
          }}
        >
          Enviar
        </button>
      </form>
    </main>
  );
};

export default Home;
