import React, { useState } from "react";
import { useParams } from "react-router-dom";

const Adotar = () => {
  const { id } = useParams();
  const [form, setForm] = useState({ nome: "", email: "", mensagem: "" });
  const [enviado, setEnviado] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setEnviado(true);
    setForm({ nome: "", email: "", mensagem: "" });
  };

  return (
    <main>
      <h1>Formulário de Adoção</h1>
      <p>Você está adotando o animal de ID: {id}</p>
      <form
        onSubmit={handleSubmit}
        style={{
          background: "#f1f5f9",
          padding: "1.5rem",
          borderRadius: "10px",
          maxWidth: "400px",
          margin: "2rem auto 0 auto",
          display: "flex",
          flexDirection: "column",
          gap: "1rem",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)"
        }}
      >
        <input
          type="text"
          name="nome"
          placeholder="Seu nome"
          value={form.nome}
          onChange={handleChange}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <input
          type="email"
          name="email"
          placeholder="Seu e-mail"
          value={form.email}
          onChange={handleChange}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <textarea
          name="mensagem"
          placeholder="Por que deseja adotar?"
          value={form.mensagem}
          onChange={handleChange}
          rows={4}
          style={{ padding: "0.7rem", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <button
          type="submit"
          style={{
            background: "#2563eb",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            padding: "0.8rem",
            fontWeight: "bold",
            cursor: "pointer"
          }}
        >
          Enviar
        </button>
        {enviado && <span style={{ color: "green" }}>Solicitação enviada!</span>}
      </form>
    </main>
  );
};

export default Adotar;
