import React from "react";
import { useNavigate } from "react-router-dom";

const CardAnimal = ({ animal }) => {
  const navigate = useNavigate();
  const imagemPadrao = "https://via.placeholder.com/180x120?text=Sem+Foto";

  return (
    <div style={{
      background: "#e0e7ff",
      padding: "1rem",
      borderRadius: "12px",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      gap: "0.5rem",
      minWidth: 220
    }}>
      <img
        src={animal.foto}
        alt={animal.nome}
        style={{ width: "100%", maxWidth: 180, borderRadius: "8px", objectFit: "cover", minHeight: 120 }}
        onError={e => e.target.src = imagemPadrao}
      />
      <h3 style={{ color: "#fbb6ce", margin: "0.5rem 0" }}>{animal.nome}</h3>
      <p>Idade: {animal.idade}</p>
      <p>Sexo: {animal.sexo}</p>
      <p>Porte: {animal.porte}</p>
      <button
        style={{
          background: "#2563eb",
          color: "#fff",
          border: "none",
          borderRadius: "6px",
          padding: "0.5rem 1rem",
          fontWeight: "bold",
          cursor: "pointer",
          marginTop: "0.5rem"
        }}
        onClick={() => navigate(`/adotar/${animal.id}`)}
      >
        Quero Adotar
      </button>
    </div>
  );
};

export default CardAnimal;
